namespace Icinga\Module\Selenium\Controllers;

use Icinga\Exception\ConfigurationError;

use Icinga\Module\Selenium\ProjectRestrictor;
use Icinga\Module\Selenium\TestsuiteRestrictor;
use Icinga\Module\Selenium\Common\Database;
use Icinga\Module\Selenium\Controller;
use Icinga\Module\Selenium\Model\Testsuite;

use Icinga\Module\Selenium\TestsuiteTable;
use Icinga\Module\Selenium\Web\Control\SearchBar\ObjectSuggestions;


use ipl\Web\Control\LimitControl;
use ipl\Web\Control\SortControl;

use ipl\Web\Url;
use ipl\Web\Widget\ButtonLink;

class TestsuitesController extends Controller
{
    public function init()
    {
        $this->assertPermission('selenium/testsuite');
        parent::init();
    }
    public function indexAction()
    {

        if ($this->hasPermission('selenium/testsuite/modify')) {
            $this->addControl(
                (new ButtonLink(
                    $this->translate('New Testsuite'),
                    Url::fromPath('selenium/testsuite/new'),
                    'plus'
                ))->openInModal()
            );
        }

        $this->addTitleTab($this->translate('Testsuites'));

        try {
            $conn = Database::get();
        } catch (ConfigurationError $_) {
            $this->render('missing-resource', null, true);
            return;
        }

        $models = Testsuite::on($conn)
            ->with(['project'])
            ->withColumns([]);


        $sortColumns = [
            'name' => $this->translate('Name'),

        ];
        $restrictor = new TestsuiteRestrictor();
        $restrictor->applyRestrictions($models);

        $restrictor2 = new ProjectRestrictor();
        $restrictor2->applyRestrictions($models);

        $limitControl = $this->createLimitControl();
        $paginator = $this->createPaginationControl($models);
        $sortControl = $this->createSortControl($models, $sortColumns);

        $searchBar = $this->createSearchBar($models, [
            $limitControl->getLimitParam(),
            $sortControl->getSortParam()
        ]);

        if ($searchBar->hasBeenSent() && ! $searchBar->isValid()) {
            if ($searchBar->hasBeenSubmitted()) {
                $filter = $this->getFilter();
            } else {
                $this->addControl($searchBar);
                $this->sendMultipartUpdate();

                return;
            }
        } else {
            $filter = $searchBar->getFilter();
        }

        $models->peekAhead($this->view->compact);

        $models->filter($filter);

        $this->addControl($paginator);
        $this->addControl($sortControl);
        $this->addControl($limitControl);
        $this->addControl($searchBar);

        $this->addContent((new TestsuiteTable())->setData($models));

        if (! $searchBar->hasBeenSubmitted() && $searchBar->hasBeenSent()) {
            $this->sendMultipartUpdate(); // Updates the browser search bar
        }
    }

    public function completeAction()
    {
        $this->getDocument()->add(
            (new ObjectSuggestions())
                ->setModel(Testsuite::class)
                ->forRequest($this->getServerRequest())
        );
    }

    public function searchEditorAction()
    {
        $editor = $this->createSearchEditor(Testsuite::on(Database::get()), [
            LimitControl::DEFAULT_LIMIT_PARAM,
            SortControl::DEFAULT_SORT_PARAM
        ]);

        $this->getDocument()->add($editor);
        $this->setTitle(t('Adjust Filter'));
    }


}
